import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
// import { CenterFocusStrong } from '@material-ui/icons';

// import './menu-item.styles.scss';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const MenuItem = ({ title, imageUrl, size }) => {
  const classes = useStyles();
  return (
  <div className={classes.root}>

    <Grid container spacing={6}>

   {/*}    
        <Grid item xs={12} >

        <div className='background-image'  style={{backgroundImage: `url(${imageUrl})` , height:150, textAlign:'center' }} >{title.toUpperCase()}</div>
        <span className='subtitle'>SHOP NOW</span>
        </Grid>
  */}
        <Grid item xs={12} >

        <Paper className='title' style={{backgroundImage: `url(${imageUrl})` , height:150, textAlign:'center' }}>{title.toUpperCase()}</Paper>
        </Grid>

    </Grid>

  </div>
);
}


export default MenuItem;
